		type: TextType.BodyMd,
		weight: TextWeight.Regular,
		color: Colors.TextHeadings,
		align: Align.Start,
		lineClampCount: 2,
		isLineClamp: false,
		isEllipsis: false,
		isUnderline: false,
	},
}

{
	parameters: {
		controls: {
			matchers: {
				color: /(background|color)$/i,
				date: /Date$/i,
			},
		},
		darkMode: {
			dark: darkTheme,
			light: lightTheme,
			darkClass: 'dark',
			lightClass: 'light',
			stylePreview: true,
		},
		docs: {
			theme: lightTheme,
		},
	},
	decorators: [
		(storyFn) => {
			const isDark = useDarkMode();
			// Меняем фон для Canvas
			document.body.style.backgroundColor = isDark
				? '#191919'
				: '#fafaf9';
			document.body.classList.toggle('dark', isDark);
			document.body.classList.toggle('light', !isDark);

			const previewContainer = document.querySelector(
				'.sbdocs-preview',
			) as HTMLElement | null;
			if (previewContainer) {
				previewContainer.style.backgroundColor = isDark
					? '#191919'
					: '#fafaf9';
			}

			return storyFn();
		},
	],
	tags: ['autodocs'],
}

